import React, { Component } from 'react';
import './pages.css'
import api from '../services/api';

class Ocorrencias extends Component {

    state = {
        search: '',
        ocorrencias: [],
    };

    async componentDidMount(){
        //const response = await api.get('users');
        //this.setState({users: response.data});
        //console.log(this.state.users);
    }


    handleChange = e => {
        this.setState({ [e.target.name]: e.target.value });
        //console.log(e.target.value);
    }


    render(){

        function showListar(){       
            change();

            var x = document.getElementById("listar-usuarios");
            x.style.display = "block";
        }

        function showCadastrar(){
            change();

            var x = document.getElementById("cadastrar-usuarios");
            x.style.display = "block";
        }

        function change(){
            var x = document.getElementById("btn-listar");
            x.style.display = "none";

            var y = document.getElementById("btn-cadastrar");
            y.style.display = "none";
        }

        return (
            <div id="app">
                <button onClick={showListar} id="btn-listar">Listar</button>
                <button onClick={showCadastrar} id="btn-cadastrar">Cadastrar</button>

                <div id="show-listar">
                    <input 
                        type="text"
                        name="search"
                        placeholder="Search ..."
                        onChange={this.handleChange}
                        value={this.state.search}
                    />

                    <section id="table">
                        <table id="users">
                            <tbody>

                                <tr id="table-header">
                                    <th>Name</th>
                                    <th>Email</th>
                                </tr>

                                { this.state.users.map(user => (
                                    <tr>
                                        <th>{user.name}</th>
                                        <th>{user.email}</th>
                                    </tr>                          
                                ))} 

                            </tbody>    
                        </table>              
                    </section>

                </div>
            </div>
        );
    }
}

export default Ocorrencias;
