import React, { Component } from 'react';
import './pages.css'
import SearchField from "react-search-field";
import ReactTable from 'react-table'
import 'react-table/react-table.css'

class Ocorrencias extends Component {

    state = {
        search: '',
    };

    search = async e => {
        e.preventDefault();

        var searchElement = document.getElementsByClassName("react-search-field-input");
        console.log(searchElement.value);
        console.log(searchElement.text);

        //var tableElement = document.getElementsByClassName("rt-td");
        //console.log(tableElement);            
    }

    handleChange = e => {
        this.setState({ [e.target.name]: e.target.value });
    }


    render(){

        const data = [{
            name: 'Explos達o do Centro',
            local: 'Centro de Curitiba',
            datetime: '20/04/2007 22:20',
            type: 'Explos達o'
        },{
            name: 'Suspeita de bomba no Bacacheri',
            local: 'Bacacheri',
            datetime: '5/03/2017 03:20',
            type: 'Suspeita de bomba'
        }]

        const columns = [{
            Header: 'Name',
            accessor: 'name'
        },{
            Header: 'Local',
            accessor: 'local'
        },{
            Header: 'Date/Time',
            accessor: 'datetime'
        },{
            Header: 'Type',
            accessor: 'type'
        }]


        function listar(){       
            var x = document.getElementById("btn-listar");
            x.style.display = "none";

            var y = document.getElementById("btn-cadastrar");
            y.style.display = "none";

            var z = document.getElementById("show-listar");
            z.style.display = "block";
        }

        return (
            <div id="app">
                <button onClick={listar} id="btn-listar">Listar</button>
                <button id="btn-cadastrar">Cadastrar</button>

                <div id="show-listar">
                    <input 
                        type="text"
                        name="search"
                        placeholder="Search ..."
                        onChange={this.search}
                        value={this.state.search}
                    />

                    <ReactTable
                        data={data}
                        columns={columns}                                      
                    />
                </div>
            </div>
        );
    }
}

export default Ocorrencias;
