import React, { Component } from 'react';
import './pages.css'
import SearchField from "react-search-field";
import ReactTable from 'react-table'
import 'react-table/react-table.css'

class Ocorrencias extends Component {

    render(){

        const data = [{
            name: 'Explos達o do Centro',
            local: 'Centro de Curitiba',
            datetime: '20/04/2007 22:20',
            type: 'Explos達o'
          },{
            name: 'Suspeita de bomba no Bacacheri',
            local: 'Bacacheri',
            datetime: '5/03/2017 03:20',
            type: 'Suspeita de bomba'
          }


            /*
            {
            name: 'Barbara Ferraz',
            age: 24,
            friend: {
              name: 'Nathalia Graton',
              age: 23,
            }
            */
        ]

        const columns = [
            {
                Header: 'Name',
                accessor: 'name' // String-based value accessors!
            }, 
            {
                Header: 'Local',
                accessor: 'local'
            },
            {
                Header: 'Date/Time',
                accessor: 'datetime'
            },
            {
                Header: 'Type',
                accessor: 'type'
            }

            /*
            {
            Header: 'Age',
            accessor: 'age',
            Cell: props => <span className='number'>{props.value}</span> // Custom cell components!
            },
            {
            id: 'friendName', // Required because our accessor is not a string
            Header: 'Friend Name',
            accessor: d => d.friend.name // Custom value accessors!
            },
            {
            Header: props => <span>Friend Age</span>, // Custom header components!
            accessor: 'friend.age'
            }*/
        ]


        function listar(){

            var x = document.getElementById("btn-listar");
            x.style.display = "none";

            var y = document.getElementById("btn-cadastrar");
            y.style.display = "none";

            var z = document.getElementById("show-listar");
            z.style.display = "block";

        }

        return (
            <div id="app">
                <button onClick={listar} id="btn-listar">Listar</button>
                <button id="btn-cadastrar">Cadastrar</button>

                <div id="show-listar">
                    <SearchField
                        placeholder="Search..."
                        classNames="test-class"
                    />
                    <ReactTable
                        data={data}
                        columns={columns}                    
                    />
                </div>

            </div>
        );
    }
}



export default Ocorrencias;
