import React, { Component } from 'react';
import './pages.css'
import api from '../services/api';

class Glossario extends Component {

    state = {
        search: '',
        terms: [],
    };

    async componentDidMount(){
        const response = await api.get('terms');
        this.setState({terms: response.data});       
    }

    handleChange = e => {
        this.setState({ [e.target.name]: e.target.value });
    }

    handleSubmit = async e => {
        e.preventDefault();      
        const response = await api.post(`/terms/${this.state.search}`);
        this.setState({terms: response.data});
    }

    render(){
        return (
            <div id="app">
                <p>GLOSSÁRIO</p>

                <div id="search-terms">
                    <form id="search-box" onSubmit={this.handleSubmit}>
                        <input 
                            type="text"
                            name="search"
                            placeholder="Procurar por nome ..."
                            onChange={this.handleChange}
                            value={this.state.search}
                        />
                        <button type="submit">Buscar</button>
                    </form>

                    <section id="alphabet">
                        <ul>
                            <li>A</li>
                            <li>B</li>
                            <li>C</li>
                            <li>D</li>
                            <li>E</li>
                            <li>F</li>
                            <li>G</li>
                            <li>H</li>
                            <li>I</li>
                            <li>J</li>
                            <li>K</li>
                            <li>L</li>
                            <li>M</li>
                            <li>N</li>
                            <li>O</li>
                            <li>P</li>
                            <li>Q</li>
                            <li>R</li>
                            <li>S</li>
                            <li>T</li>
                            <li>U</li>
                            <li>V</li>
                            <li>W</li>
                            <li>X</li>
                            <li>Y</li>
                            <li>Z</li>    
                        </ul>
                    </section>

                    <section id="table">
                        <table id="terms">
                            <tbody>
                                <tr id="table-header">
                                    <th>Termo</th>
                                    <th>Descrição</th>
                                </tr>                           
                                { this.state.terms.map(term => (
                                    <tr id="table-data" key={term.id}>
                                        <th>{term.termo}</th>
                                        <th>{term.descricao}</th>
                                    </tr>                         
                                ))}                                    
                            </tbody>    
                        </table>              
                    </section>

                </div>
            </div>
        );
    }
}

export default Glossario;
