import React, { Component } from 'react';
import './pages.css'
import api from '../services/api';

class Usuarios extends Component {

    state = {
        search: '',
        users: [],
    };

    async componentDidMount(){
        const response = await api.get('users');
        this.setState({users: response.data});
    }


    handleChange = e => {
        this.setState({ [e.target.name]: e.target.value });
    }

    handleSubmit = async e => {
        e.preventDefault();      
        console.log(e);

        const response = await api.post(`/users/${this.state.search}`);
        this.setState({users: response.data});
    }

    render(){

        function listar(){       
           change();

            var z = document.getElementById("listar-usuarios");
            z.style.display = "block";
        }

        function cadastrar(){
            change();

        }

        function change(){
            var x = document.getElementById("btn-listar");
            x.style.display = "none";

            var y = document.getElementById("btn-cadastrar");
            y.style.display = "none";
        }

        return (
            <div id="app">
                <button onClick={listar} id="btn-listar">Listar</button>
                <button onClick={cadastrar} id="btn-cadastrar">Cadastrar</button>

                <div id="listar-usuarios">
                    <form id="search-box" onSubmit={this.handleSubmit}>
                        <input 
                            type="text"
                            name="search"
                            placeholder="Search by name ..."
                            onChange={this.handleChange}
                            value={this.state.search}
                        />
                        <button type="submit">Search</button>
                    </form>
                    <section id="table">
                        <table id="users">
                            <tbody>
                                <tr id="table-header">
                                    <th>Name</th>
                                    <th>Email</th>
                                </tr>
                                { this.state.users.map(user => (
                                    <tr key={user.id}>
                                        <th>{user.name}</th>
                                        <th>{user.email}</th>
                                    </tr>                         
                                ))} 
                            </tbody>    
                        </table>              
                    </section>
                </div>

                <div id="cadadastrar-usuarios">
                    <form id="cadastro" onSubmit={this.handleSubmit}>

                    </form>
                </div>

            </div>
        );
    }
}

export default Usuarios;
