import React, { Component } from 'react';
import './pages.css'
import api from '../services/api';

class Ocorrencias extends Component {

    state = {
        search: '',
        users: [],
    };

    async componentDidMount(){
        const response = await api.get('users');
        this.setState({users: response.data});
        console.log(this.state.users);
    }


    handleChange = e => {
        this.setState({ [e.target.name]: e.target.value });
        console.log(e.target.value);
    }


    render(){

        function listar(){       
            var x = document.getElementById("btn-listar");
            x.style.display = "none";

            var y = document.getElementById("btn-cadastrar");
            y.style.display = "none";

            var z = document.getElementById("show-listar");
            z.style.display = "block";
        }

        return (
            <div id="app">
                <button onClick={listar} id="btn-listar">Listar</button>
                <button id="btn-cadastrar">Cadastrar</button>

                <div id="show-listar">
                    <input 
                        type="text"
                        name="search"
                        placeholder="Search ..."
                        onChange={this.handleChange}
                        value={this.state.search}
                    />

                    <section id="table">
                        { this.state.users.map(user => (
                            <article key={user.id}>
                                <table>
                                    <tbody>
                                        <tr>
                                            <th>(user.id}</th>
                                            <th>{user.name}</th>
                                            <th>{user.email}</th>
                                        </tr>
                                    </tbody>
                                </table>
                            </article>       
                        ))}
                    </section>

                </div>
            </div>
        );
    }
}

export default Ocorrencias;
