import React, { Component } from 'react';
import './pages.css'
import api from '../services/api';

class Ocorrencias extends Component {

    state = {
        search: '',
        ocorrencias: [],
    };

    async componentDidMount(){
        //const response = await api.get('users');
        //this.setState({users: response.data});
        //console.log(this.state.users);
    }


    handleChange = e => {
        this.setState({ [e.target.name]: e.target.value });
        //console.log(e.target.value);
    }


    render(){

        function showListar(){       
            change();

            var x = document.getElementById("listar-ocorrencias");
            x.style.display = "block";
        }

        function showCadastrar(){
            change();

            var x = document.getElementById("cadastrar-ocorrencias");
            x.style.display = "block";
        }

        function change(){
            var x = document.getElementById("btn-listar");
            x.style.display = "none";

            var y = document.getElementById("btn-cadastrar");
            y.style.display = "none";
        }

        return (
            <div id="app">
                <button onClick={showListar} id="btn-listar">Listar</button>
                <button onClick={showCadastrar} id="btn-cadastrar">Cadastrar</button>

                <div id="listar-usuarios">
                    <form id="search-box" onSubmit={this.handleSubmit}>
                        <input 
                            type="text"
                            name="search"
                            placeholder="Procurar por nome ..."
                            onChange={this.handleChange}
                            value={this.state.search}
                        />
                        <button type="submit">Buscar</button>
                    </form>
                    <section id="table">
                        <table id="users">
                            <tbody>
                                <tr id="table-header">
                                    <th>Name</th>
                                    <th>Email</th>
                                </tr>
                                { this.state.users.map(user => (
                                    <tr key={user.id}>
                                        <th>{user.name}</th>
                                        <th>{user.email}</th>
                                    </tr>                         
                                ))} 
                            </tbody>    
                        </table>              
                    </section>
                </div>

                <div id="cadastrar-usuarios">
                    <form id="new-user" onSubmit={this.handleFormSubmit}>
                        <input                             
                            type="text"
                            name="name"
                            placeholder="Digite o nome ..."
                            onChange={this.handleChange}
                            value={this.state.name}
                        />
                        <input                             
                            type="text"
                            name="email"
                            placeholder="Digite o email ..."
                            onChange={this.handleChange}
                            value={this.state.email}
                        />
                        <input                             
                            type="text"
                            name="password"
                            placeholder="Digite a senha ..."
                            onChange={this.handleChange}
                            value={this.state.password}
                        />
                        <button type="submit" >Cadastrar</button>
                    </form>
                </div>

            </div>
        );
    }
}

export default Ocorrencias;
